    // Start is called before the first frame update
    public MapHexElement SetHex(int i, int j)
    {
        MapHexElement hexElement = new MapHexElement();
        //地形元素
        hexElement.terrain = MapTerrain[i, j];
        //建筑元素
        hexElement.construction = MapConstruction[i, j];
        //河流
        while (MapRiver[i, j] > 0)
        {
            
            int a = MapRiver[i, j] % 10;
            hexElement.rivers[a - 1] = 1;
            MapRiver[i, j] /= 10;
        }
        // 道路元素
        if (MapRoad[i, j] != "0")
        {
            int n = 0;
            // 先不处理一个六边形格子里边有两条没有交点的道路，先默认它们是相交的
            if (MapRoad[i, j][0] == 'o') n++;
            while (n < MapRoad[i, j].Length)
            {        
                hexElement.roads[int.Parse(MapRoad[i, j][n++] + "") - 1] = 1;
            }
        }
        return hexElement;
    }
